            };");

    if (enumToGenerate.HasFlags) {
      sb.Append(@"

        public static bool HasFlag(this ").Append(enumToGenerate.FullyQualifiedName).Append(@" value, ")
        .Append(enumToGenerate.FullyQualifiedName).Append(@" flag)
            => value switch
            {
