    }

    sb.Append(@"

       public static bool IsDefined(").Append(enumToGenerate.FullyQualifiedName).Append(@" value)
            => value switch
            {");
    foreach (KeyValuePair<string, object> member in enumToGenerate.Values) {
      sb.Append(@"
