                case { } s when s.Equals(nameof(").Append(enumToGenerate.FullyQualifiedName).Append('.')
        .Append(key).Append(@"), System.StringComparison.OrdinalIgnoreCase):
                    value = ").Append(enumToGenerate.FullyQualifiedName).Append('.').Append(key).Append(@";
                    return true;");
